export default function showMenu(){let l=document.querySelector(".mobile-menu")||null;if(l){var o=document.querySelector(".header_menu"),n=document.querySelector(".close");let e=document.querySelector("body");let t=()=>{l.classList.remove("menuShow"),e.style.overflow="visible"};o.addEventListener("click",()=>{l.classList.add("menuShow"),e.style.overflow="hidden"}),n.addEventListener("click",t),document.addEventListener("click",e=>{e.target==l&&t()})}}